package day20_ForLoop;import java.sql.SQLOutput;import java.util.Scanner;public class BreakContinue {    public static void main(String[] args) {        for (int i = 1; i <= 10; i--) {            if (i<1){                break;            }            System.out.println("Hello");        }        System.out.println("=====================================");        Scanner scan = new Scanner(System.in);        for (int i =0; i==0; ) {            System.out.println("Enter two numbers: ");            int n1 = scan.nextInt();            int n2 = scan.nextInt();            System.out.println("Enter math operator");            char operator = scan.next().charAt(0);            switch (operator){                case '%':                    System.out.println(n1%n2);                    break;                case'+':                    System.out.println(n1+n2);                    break;                case '-':                    System.out.println(n1-n2);                    break;                case '/':                    System.out.println(n1/n2);                    break;                case'*':                    System.out.println(n1*n2);                    break;                default:                    System.out.println("Invalid operator");            }            System.out.println("Would you like to continue? yes or no?");            String answer = scan.next();if(answer.equalsIgnoreCase("no")){    System.out.println("Thank you for using our calculator");    break;}        }    }}